mapper.select:
select *
from user
where account = #{account}
  and password = #{password};

select distinct *
from user
where account = #{account}
   or telephone_number = #{telephone_number}
order by id limit 0,1;

select *
from user
where account = #{account}
  and telephone_number = #{telephone_number};

mapper.update:
update user
set password = #{password}
where account = #{account};
